function blockUI(n){$(n).prepend("<div class='blockUI'><img src='images/loader.gif'></div>")}function unblockUI(n){$(n).find(".blockUI").fadeOut("slow",function(){$(this).remove()})}function validateEmail(n){var a=/^([\w-]+(?:\.[\w-]+)*)@((?:[\w-]+\.)*\w[\w-]{0,66})\.([a-z]{2,6}(?:\.[a-z]{2})?)$/i;return a.test(n)}function timeDifference(n){var a=new Date,o=6e4,r=60*o,t=24*r,e=30*t,i=365*t,u=a-new Date(n);if(o>u)return"one minute ago";if(r>u){var d=" minute ago";return Math.round(u/o)>1&&(d=" minutes ago"),Math.round(u/o)+d}if(t>u)return Math.round(u/r)+" hours ago";if(e>u){var d=" day ago";return Math.round(u/t)>1&&(d=" days ago"),Math.round(u/t)+d}return i>u?Math.round(u/e)+" months ago":Math.round(u/i)+" years ago"}